&lt;p&gt;&lt;a href=&quot;/img/wqeLc_-VSA-1799.jpeg&quot;&gt;&lt;picture&gt;&lt;source type=&quot;image/avif&quot; srcset=&quot;/img/wqeLc_-VSA-600.avif 600w&quot; sizes=&quot;100vw&quot;&gt;&lt;source type=&quot;image/webp&quot; srcset=&quot;/img/wqeLc_-VSA-600.webp 600w&quot; sizes=&quot;100vw&quot;&gt;&lt;img alt=&quot;&quot; loading=&quot;lazy&quot; decoding=&quot;async&quot; class=&quot;u-photo visible&quot; src=&quot;/img/wqeLc_-VSA-600.jpeg&quot; width=&quot;600&quot; height=&quot;1216&quot;&gt;&lt;/picture&gt;&lt;/a&gt;&lt;/p&gt;
